import { ApiProperty } from '@nestjs/swagger'
import {
  ArrayNotEmpty,
  Equals,
  IsArray,
  IsHexadecimal,
  IsInt,
  IsString,
  Min,
  MinLength,
} from 'class-validator'

export class RtVotingCreateRequestDTO {
  @ApiProperty({ example: '507f1f77bcf86cd799439011' })
  @IsString()
  @MinLength(1)
  pollId: string

  @ApiProperty({
    type: String,
    example: 'blind',
    enum: ['blind'],
  })
  @IsString()
  @Equals('blind')
  type: 'blind'

  @ApiProperty({
    type: String,
    description: 'Blind signature modulo',
    example: '',
  })
  @IsHexadecimal()
  @IsString()
  blindSigModulo: string

  @ApiProperty({
    type: String,
    description: 'Blind signature exponent',
    example: '10001',
  })
  @IsString()
  @IsHexadecimal()
  blindSigExponent: string

  @ApiProperty({ example: '5ec3f6a79frgwqgrgeetqgd339d4d135f3dba' })
  @IsString()
  @MinLength(1)
  bulletinHash: string

  @ApiProperty({ type: [Number], example: [1, 2, 3] })
  @IsArray()
  @ArrayNotEmpty()
  @IsInt({ each: true })
  @Min(1, { each: true })
  dimension: number[]

  @ApiProperty({ example: Date.now() + 5 * 60000 })
  @IsInt()
  dateStart: number

  dateEnd: number
  title: string
  description: string
  companyId: string
  docs: object[]
  admins: string[]
}
